mapper.update:
update teacher
set name = #{name,jdbcType=VARCHAR},
  email = #{email,jdbcType=VARCHAR},
  phone = #{phone,jdbcType=VARCHAR},
  address_id = #{addressId,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, name, email, phone, address_id
from teacher
where id = #{id,jdbcType=INTEGER}

